X = randn(500,3);cov(X)corrcoef(X)[n p] = size(X);vect1 = ones(n,1);mu = mean(X);st = std(X);Xcr = (X - mu(vect1,:))./st(vect1,:);disp(Xcr);echelle = repmat([floor(min(Xcr(:))) ceil(max(Xcr(:)))], 1, 2);for i = 1:p      for j = 1:p            subplot(p,p,(i-1)*p+j);            plot(Xcr(:,i),Xcr(:,j),'.');            axis(echelle);            grid;      endendfigure;X2 = X * [ 2 0 0 ; 0 4 0 ; 1 -1 1 ];cov(X2)corrcoef(X2)[n p] = size(X2);vect2 = ones(n,1);mu2 = mean(X2);st2 = std(X2);X2cr = (X2 - mu2(vect2,:))./st2(vect2,:);echelle = repmat([floor(min(X2cr(:))) ceil(max(X2cr(:)))], 1, 2);for i = 1:p      for j = 1:p            subplot(p,p,(i-1)*p+j);            plot(X2cr(:,i),X2cr(:,j),'.');            axis(echelle);            grid;      endendfigure;[V, LAMBDA] = eig (corrcoef(X2cr))Y = X2cr * V;cov(Y)corrcoef(Y)[n p] = size(Y);vect3 = ones(n,1);muY = mean(Y);stY = std(Y);Ycr = (Y - muY(vect3,:))./stY(vect3,:);echelle = repmat([floor(min(Ycr(:))) ceil(max(Ycr(:)))], 1, 2);for i = 1:p      for j = 1:p            subplot(p,p,(i-1)*p+j);            plot(Ycr(:,i),Ycr(:,j),'.');            axis(echelle);            grid;      endendfigure;